        msg.payload=input;
    msg.topic="result";
    msg.flow=msg.flow||flow.get("AT_Flow");
    //flow.set("ATCMD_B","");
    return msg;
    }
}

if (command.includes("AT+TTX")) {
    if (msg.payload.includes(":Tx ")) {
        msg.payload=input;
    return msg;
