}

if (command==="ATZ") {
    if (input.includes("###### GenAppKey:")) {
        msg.payload="OK";
        msg.topic="resetok";
        flow.set("ATCMD_B","");
        msg.flow=flow.get("AT_Flow");
        return msg;
    }
}


switch (input) {
