        status="error";
        flow.set("ATCMD_B","");
        break;
    case "AT_BUSY_ERROR":
        status="error";
        flow.set("ATCMD_B","");
        break;


}
if (status !== null) {
    msg.topic=status;
    msg.flow=msg.flow||flow.get("AT_Flow");

return msg;
}
else {
    return null;

}

if (flow.get("ATCMD_B")==="" || flow.get("ATCMD_B")===undefined) {

// No AT cmd ongoing
flow.set("ATCMD_B",msg.payload);
flow.set("AT_Flow",msg.flow);
return msg;
}

// busy case
var errorMsg = { topic : "error", flow : msg.flow, payload : "BUSY_B" };
return [null, errorMsg];

const timeoutValue=7000;
let timeoutId=context.get("timeoutId");
let kickCount=context.get("kickCount")||0;

if (timeoutId!==undefined) {
    clearTimeout(timeoutId);
}

if (msg.topic==="ok_a" || msg.topic==="ok_b"|| msg.topic==="resetok" || msg.topic==="error") {
    node.status({fill:"green",shape:"dot",text:"stopped"});                    
    context.set("kickCount",0);
    return null;
}

timeoutId=setTimeout(function() {
            node.status({fill:"red",shape:"dot",text:"TIMEOUT"});                    
            var timMsg={ payload:"timeout B",
                        status: {text: "timeout"}};
            node.send(timMsg);
            }, timeoutValue);

context.set("timeoutId",timeoutId);

kickCount= kickCount+1;     
context.set("kickCount",kickCount);
node.status({text:"kicked "+kickCount});                    

return null;

msg.payload = "AT+TCONF=" + flow.get("Frequency")*1000000+ ":14:75000:50000:4/5:0:0:0:255:25000:2:3\n";
msg.flow="TONERX";
return msg;

msg.payload="AT+TRSSI\n"
msg.flow="TONERX";
return msg;

msg.payload="ATZ\n"
msg.flow="TONERX";
return msg;

if (msg.payload !== undefined) {
    flow.set(msg.topic,msg.payload);
}
return msg;

if (msg.flow==="TONERX" || msg.flow==="ALL") {
return msg;
} else {
