}

var input = msg.payload ||"";
var pos= input.search("RSSI Value=");
if (pos>1){
    msg.payload=input.substring(pos+12);
    return msg;
} else {
return null;
}

msg.payload = "AT+TCONF=" + flow.get("Frequency")*1000000+ ":"+flow.get("Power")+":75000:50000:4/5:0:0:0:255:25000:2:3\n";
