msg.payload = "AT+TCONF=" + flow.get("FrequencyTx")*1000000+ ":"+flow.get("Power")+":75000:50000:4/5:0:0:0:255:25000:2:3\n";

msg.flow="TONERXTX";
return msg;

msg.payload="AT+TTONE\n"
