}

if (msg.topic==="controlRx") {
    context.set ("enableRx", msg.enabled);
}
if (msg.topic==="controlTx") {
    context.set ("enableTx", msg.enabled);
}
if (context.get("enableRx")===false || context.get("enableTx")===false) {
    msg.enabled=false;
} else {
    msg.enabled=true;
}
return msg;

if (msg.payload !== undefined) {
