var PL = context.get('PL') || 51;
var nPreamble = context.get('nPreamble') || 8;
var H = context.get('H') || 0;
var CRC = context.get('CRC') || 1;
var DE = context.get('DE') || 0;

// Update values
if(msg.topic === 'SF'){
    context.set('SF', msg.payload);
    SF = msg.payload;
}
if(msg.topic === 'BW'){
    context.set('BW', msg.payload);
    BW = msg.payload;
}

if(msg.topic === 'CR'){
    context.set('CR', msg.payload);
    CR = msg.payload;
}

if(msg.topic === 'PayloadLength'){
    context.set('PL', msg.payload);
    PL = msg.payload;
}

if(msg.topic === 'PreambleLength'){
    context.set('nPreamble', msg.payload);
    nPreamble = msg.payload;
}

if(msg.topic === 'ExplicitHeader'){
    context.set('H', msg.payload);
    H = msg.payload;
}

if(msg.topic === 'CRC'){
    context.set('CRC', msg.payload);
    CRC = msg.payload;
}

//LowdataRateoptimization
if(SF >= 11 && BW === 125){
    LowDataRateOptimization = "Yes";
    context.set('DE',1);
    DE = 1;
} else {
    var LowDataRateOptimization = "No";
    context.set('DE',0);
    DE = 0;
}

//SymbolLength 
var SymbolLength = (Math.pow(2,SF))/BW;

//PreambleLength
var PreambleLength = (nPreamble + 4.25) * SymbolLength;

//SymbolLenght
var NbSymbolsFrame = 8+Math.max(Math.ceil((8*PL-4*SF+28+16*CRC-20*H)/(4*(SF-2*DE)))*(CR+4),0);

//TimeOnAir
var TimeOnAir = (NbSymbolsFrame * SymbolLength) + PreambleLength;

//DutyCycle
var DutyCycle = new Date(Math.round((TimeOnAir / 600) * 60) * 1000);

var min = DutyCycle.getMinutes();
var sec = DutyCycle.getSeconds();


msg.payload = "newValue";
msg.topic = "airtime";
msg.LowDataRateOptimization = LowDataRateOptimization;
msg.PreambleLength = Math.round(PreambleLength*100)/100;
msg.SymbolLength = Math.round(SymbolLength*100)/100;
msg.NbSymbolsFrame = NbSymbolsFrame;
msg.TimeOnAir = Math.round(TimeOnAir*100)/100;
msg.DutyCycle = (min  < 10 ? '0' : '') + min + ":" + (sec  < 10 ? '0' : '') + sec;

return msg;
